mapper.select:
select p.on_no, p.pro_code, p.pro_name, p.userid, p.status as pd_status, o.status, DATE_FORMAT(o.orderdate, '%m%d%H%i') orderdate, DATE_FORMAT(p.writedate, '%m%d%H%i') writedate from (select pd.on_no, pd.pro_code, p.pro_name, pd.userid, pd.status, pd.writedate from product_detail pd join product p on pd.pro_code=p.pro_code and pd.status=2) p join order09 o on p.on_no=o.on_no and p.userid=o.userid
and ${searchKey} like concat('%', #{searchWord}, '%')
order by on_no desc LIMIT #{limitStart}, #{onePageRecord}

select o.ord_no, o.pro_code, o.userid, o.ord_count, o.status, o.on_no, o.orderdate, o.ord_amount, p.pro_name from order09 o join product p on o.pro_code=p.pro_code
where ${searchKey} like concat('%', #{searchWord}, '%')
order by ord_no desc LIMIT #{limitStart}, #{onePageRecord}

     select row_number() over(order by userid desc) as num, username, userid, tel, email, ranking, writedate, report 
     from registerinfo where userid not in (select userid from temp_stop)

        and ${searchKey} like concat('%', #{searchWord}, '%')

     order by username asc 
	  LIMIT #{limitStart}, #{onePageRecord}

select report_no, mem_id, target_id, report_content, DATE_FORMAT(writedate, '%m-%d %H:%i') writedate, target_type from report
where ${searchKey} like concat('%', #{searchWord}, '%')
order by report_no desc LIMIT #{limitStart}, #{onePageRecord}

select pro_code, pro_name, pro_price, pro_stock, image, detailed, pro_total from product
where ${searchKey} like concat('%', #{searchWord}, '%')
order by pro_code desc LIMIT #{limitStart}, #{onePageRecord}

select r.username, r.userid, r.tel, r.email, r.ranking, DATE_FORMAT(t.writedate,'%m-%d %H:%i') writedate, r.report from registerinfo r join temp_stop t on r.userid=t.userid
and ${searchKey} like concat('%', #{searchWord}, '%')
order by writedate desc LIMIT #{limitStart2}, #{onePageRecord2}

select r.report_no, r.mem_id, r.target_id, r.report_content, date_format(r.writedate,'%m-%d %H:%i') writedate, u.report from report r join registerinfo u on r.target_id = u.userid
and ${searchKey} like concat('%', #{searchWord}, '%')
order by writedate desc LIMIT #{limitStart}, #{onePageRecord}

select cus_b_num, category , subject, userid, date_format(writedate,'%m-%d %H:%i') writedate from cus_center
where ${searchKey} like concat('%', #{searchWord}, '%')
order by cus_b_num desc LIMIT #{limitStart}, #{onePageRecord}


select cus_b_num, category, c.subject, c.content, r.userid, date_format(c.writedate,'%m-%d %H:%i') writedate
from cus_center c join registerinfo r on c.userid=r.userid
and c.cus_b_num=${param1}

select b.off_no, b.userid, b.off_subject, b.off_content, b.location, b.current_num, b.group_num, date_format(b.app_time, '%m-%d') app_time, date_format(b.deaddate, '%m-%d') deaddate, r.username, b.off_hit, date_format(b.writedate,'%m-%d') writedate , b.status from off_gb b join registerinfo r on b.userid=r.userid
and ${searchKey} like concat('%', #{searchWord}, '%')
order by b.off_no desc LIMIT #{limitStart}, #{onePageRecord}
